mapper.update:
UPDATE fdcs_admin_user_login_log
adminUserId=#{adminUserId}, adminUserName=#{adminUserName}, adminRealName=#{adminRealName}, browser=#{browser}, ip=#{ip}, state=#{state}, createTime=#{createTime},
WHERE id=#{id} ;

mapper.select:
SELECT * FROM fdcs_admin_user_login_log

    1=1
    AND adminUserId=#{adminUserId}
    AND adminUserName=#{adminUserName}
    AND adminRealName=#{adminRealName}
    AND browser=#{browser}
    AND ip=#{ip}
    AND state=#{state}
    AND createTime=#{createTime}

limit 0,1

SELECT * FROM fdcs_admin_user_login_log

    1=1
    AND adminUserId=#{adminUserId}
    AND adminUserName=#{adminUserName}
    AND adminRealName=#{adminRealName}
    AND browser=#{browser}
    AND ip=#{ip}
    AND state=#{state}
    AND createTime=#{createTime}

ORDER BY id DESC
