mapper.update:
UPDATE fdcs_info_ggdic_content
dicPath=#{dicPath}, title=#{title}, linkUrl=#{linkUrl}, picUrl=#{picUrl}, number1=#{number1}, float1=#{float1}, intro=#{intro}, adminUserId=#{adminUserId}, adminRealName=#{adminRealName}, orderIndex=#{orderIndex}, state=#{state},
WHERE id=#{id} ;

mapper.select:
SELECT * FROM fdcs_info_ggdic_content

    1=1
    AND id=#{id}
    AND dicPath=#{dicPath}
    AND title=#{title}
    AND linkUrl=#{linkUrl}
    AND picUrl=#{picUrl}
    AND number1=#{number1}
    AND float1=#{float1}
    AND intro=#{intro}
    AND adminUserId=#{adminUserId}
    AND adminRealName=#{adminRealName}
    AND orderIndex=#{orderIndex}
    AND state=#{state}


limit 0,1

SELECT * FROM fdcs_info_ggdic_content

    1=1
    AND id=#{id}
    AND dicPath=#{dicPath}
    AND title=#{title}
    AND linkUrl=#{linkUrl}
    AND picUrl=#{picUrl}
    AND number1=#{number1}
    AND float1=#{float1}
    AND intro=#{intro}
    AND adminUserId=#{adminUserId}
    AND adminRealName=#{adminRealName}
    AND orderIndex=#{orderIndex}
    AND state=#{state}

ORDER BY orderIndex DESC,id DESC
