mapper.update:
UPDATE fdcs_info_ggdic
dicName=#{dicName}, dicPath=#{dicPath}, state=#{state}, orderIndex=#{orderIndex},
WHERE id=#{id} ;

mapper.select:
SELECT * FROM fdcs_info_ggdic

    1=1
    AND id=#{id}
    AND dicName=#{dicName}
    AND dicPath=#{dicPath}
    AND state=#{state}
    orderIndex=#{orderIndex},

limit 0,1

SELECT * FROM fdcs_info_ggdic

    1=1
    AND id=#{id}
    AND dicName=#{dicName}
    AND dicPath=#{dicPath}
    AND state=#{state}
    orderIndex=#{orderIndex},

SELECT id,dicName,dicPath,state,orderIndex,substring(dicPath,1,LENGTH(DicPath)-3) as path FROM fdcs_info_ggdic

    1=1
    AND id=#{id}
    AND dicName=#{dicName}
    AND dicPath=#{dicPath}
    AND state=#{state}
