mapper.update:
UPDATE fdcs_info_suggest
userId=#{userId}, realName=#{realName}, mobile=#{mobile}, isRead=#{isRead}, content=#{content},
WHERE id=#{id} ;

mapper.select:
SELECT * FROM fdcs_info_suggest

    1=1
    AND id=#{id}
    AND userId=#{userId}
    AND realName=#{realName}
    AND mobile=#{mobile}
    AND isRead=#{isRead}
    AND content=#{content}

limit 0,1

SELECT * FROM fdcs_info_suggest

    1=1
    AND id=#{id}
    AND userId=#{userId}
    AND realName=#{realName}
    AND mobile=#{mobile}
    AND isRead=#{isRead}
    AND content=#{content}
