mapper.update:
UPDATE fdcs_sys_dictionary_content
dicPath=#{dicPath}, string1=#{string1}, string2=#{string2}, string3=#{string3}, string4=#{string4}, zs1=#{zs1}, zs2=#{zs2}, zs3=#{zs3}, zs4=#{zs4}, intro1=#{intro1}, intro2=#{intro2}, content=#{content}, orderIndex=#{orderIndex},
WHERE id=#{id} ;

mapper.select:
SELECT * FROM fdcs_sys_dictionary_content

    1=1
    AND id=#{id}
    AND dicPath=#{dicPath}
    AND string1=#{string1}
    AND string2=#{string2}
    AND string3=#{string3}
    AND string4=#{string4}
    AND zs1=#{zs1}
    AND zs2=#{zs2}
    AND zs3=#{zs3}
    AND zs4=#{zs4}
    AND intro1=#{intro1}
    AND intro2=#{intro2}
    AND content=#{content}
    AND orderIndex=#{orderIndex}

limit 0,1

SELECT * FROM fdcs_sys_dictionary_content

    1=1
    AND id=#{id}
    AND dicPath=#{dicPath}
    AND string1=#{string1}
    AND string2=#{string2}
    AND string3=#{string3}
    AND string4=#{string4}
    AND zs1=#{zs1}
    AND zs2=#{zs2}
    AND zs3=#{zs3}
    AND zs4=#{zs4}
    AND intro1=#{intro1}
    AND intro2=#{intro2}
    AND content=#{content}
    AND orderIndex=#{orderIndex}

ORDER BY orderIndex DESC,id DESC
